mapper.delete:
delete from users
where id in

    #{id}

mapper.select:
select
id, account, name, password, gender, area_name, email, idcard, mobile_num, role, amatar, sign, create_time
from users


        and id = #{id}


        and account = #{account}


        and name = #{name}


        and password = #{password}


        and gender = #{gender}


        and area_name = #{areaName}


        and email = #{email}


        and idcard = #{idcard}


        and mobile_num = #{mobileNum}


        and role = #{role}


        and amatar = #{amatar}


        and sign = #{sign}


        and create_time = #{createTime}
