mapper.delete:
delete from library.users
where id in

    #{id}

mapper.select:
select
id, account, name, password, gender, nick_name, email, idcard, mobile_num, role, amatar, remark, create_time
from library.users


        and id = #{id}


        and account = #{account}


        and name = #{name}


        and password = #{password}


        and gender = #{gender}


        and nick_name = #{nickName}


        and email = #{email}


        and idcard = #{idcard}


        and mobile_num = #{mobileNum}


        and role = #{role}


        and amatar = #{amatar}


        and remark = #{remark}


        and create_time = #{createTime}
